-- Insert 50+ советов (tips) по всем категориям
INSERT INTO public.tips (title, content, category, is_premium, "order") VALUES
-- Срочная помощь (urgent)
('Дыши глубоко', 'Когда возникает желание закурить, сделайте 10 глубоких вдохов. Вдох через нос на 4 счета, задержка на 7, выдох через рот на 8. Это успокаивает нервную систему и снижает тягу.', 'urgent', false, 1),
('Выпей воды', 'Выпейте стакан холодной воды медленными глотками. Вода помогает вывести токсины и отвлекает от желания курить.', 'urgent', false, 2),
('Пожуй что-то', 'Держите под рукой морковь, жевательную резинку без сахара или семечки. Жевание имитирует привычку курения.', 'urgent', false, 3),
('Смени обстановку', 'Переместитесь в другую комнату или выйдите на улицу. Смена окружения разрывает триггер.', 'urgent', false, 4),
('Позвони другу', 'Наберите номер близкого человека и поговорите 5 минут. Социальная поддержка критически важна.', 'urgent', false, 5),
('Техника 5-4-3-2-1', 'Назовите 5 вещей, которые видите, 4 - которые слышите, 3 - которые можете потрогать, 2 - запаха, 1 - вкус. Это заземляет вас в настоящем моменте.', 'urgent', true, 6),
('Холодный душ', 'Включите холодную воду на 30 секунд. Резкая смена температуры перезагружает нервную систему.', 'urgent', true, 7),
('Запиши свои эмоции', 'Возьмите блокнот и запишите, что вы чувствуете прямо сейчас. Это помогает осознать триггеры.', 'urgent', false, 8),
('Визуализация', 'Закройте глаза и представьте себя через год - здоровым, счастливым, без сигарет.', 'urgent', true, 9),
('Активное дыхание', 'Дышите быстро и глубоко 30 раз, затем задержите дыхание на максимальное время. Повторите 3 раза.', 'urgent', true, 10),

-- Упражнения (exercises)
('Приседания спасают', 'Сделайте 20 приседаний. Физическая активность выделяет эндорфины - природную замену никотина.', 'exercises', false, 11),
('Отжимания от стены', 'Встаньте в метре от стены и делайте отжимания. 3 подхода по 10 раз.', 'exercises', false, 12),
('Планка на минуту', 'Встаньте в планку и держите 60 секунд. Это укрепляет волю и отвлекает от тяги.', 'exercises', false, 13),
('Йога для начинающих', 'Изучите позу "собака мордой вниз" и "ребенок". Делайте 5 минут при желании курить.', 'exercises', true, 14),
('Растяжка шеи', 'Медленно наклоняйте голову в стороны, вперед, назад. Снимает напряжение от стресса.', 'exercises', false, 15),
('Прыжки на месте', '50 прыжков на месте повышают сердечный ритм и отвлекают от мыслей о курении.', 'exercises', false, 16),
('Танец 5 минут', 'Включите любимую музыку и танцуйте. Движение высвобождает дофамин.', 'exercises', true, 17),
('Прогулка быстрым шагом', 'Выйдите на 10-минутную быструю прогулку. Свежий воздух очищает легкие.', 'exercises', false, 18),
('Упражнение для рук', 'Сжимайте и разжимайте кулаки 20 раз. Помогает снять напряжение в руках.', 'exercises', false, 19),
('Велосипед лежа', 'Лягте на спину и делайте упражнение "велосипед" 2 минуты.', 'exercises', true, 20),

-- Психология (psychology)
('Поймите свои триггеры', 'Составьте список ситуаций, когда вы хотите курить: после еды, стресс, скука. Подготовьте альтернативу для каждого.', 'psychology', false, 21),
('Техника "остановись и подумай"', 'Когда появляется желание, остановитесь на 5 минут. Спросите себя: "Зачем мне это?". Часто желание проходит.', 'psychology', false, 22),
('Визуализация последствий', 'Представьте себя курящим через 10 лет: запах, кашель, болезни. Контраст мотивирует.', 'psychology', true, 23),
('Позитивные аффирмации', 'Повторяйте: "Я сильнее сигарет", "Мое тело очищается", "Я выбираю здоровье".', 'psychology', false, 24),
('Дневник благодарности', 'Каждый вечер записывайте 3 вещи, за которые благодарны. Это переключает фокус.', 'psychology', false, 25),
('Примите тягу', 'Не боритесь с желанием, а примите его как волну. Наблюдайте, как она приходит и уходит.', 'psychology', true, 26),
('Награждайте себя', 'За каждый день без сигарет делайте что-то приятное: вкусная еда, фильм, прогулка.', 'psychology', false, 27),
('Работа с убеждениями', 'Напишите все причины, почему вы курите. Затем найдите рациональные контраргументы.', 'psychology', true, 28),
('Медитация осознанности', 'Сидите тихо 10 минут, наблюдая за дыханием. Это учит контролировать импульсы.', 'psychology', true, 29),
('Прощение себя', 'Если сорвались - простите себя. Срыв - это урок, не провал. Начните заново.', 'psychology', false, 30),

-- Здоровье (health)
('Пейте зеленый чай', 'Зеленый чай ускоряет вывод никотина и содержит антиоксиданты для восстановления.', 'health', false, 31),
('Витамин C', 'Никотин разрушает витамин C. Ешьте больше цитрусовых, киви, перца для восстановления.', 'health', false, 32),
('Детокс-смузи', 'Смешайте шпинат, банан, яблоко, воду. Пейте утром для очищения организма.', 'health', true, 33),
('Здоровый сон', 'Спите 7-8 часов. Недосып усиливает тягу к курению в 3 раза.', 'health', false, 34),
('Избегайте кофе первые недели', 'Кофе - сильный триггер курения. Замените на травяной чай.', 'health', false, 35),
('Жирные кислоты Омега-3', 'Ешьте рыбу, орехи, семена льна. Омега-3 восстанавливают мозг после никотина.', 'health', true, 36),
('Контрастный душ', 'Чередуйте горячую и холодную воду 5 раз. Улучшает кровообращение и настроение.', 'health', false, 37),
('Дыхательные упражнения', 'Практикуйте диафрагмальное дыхание 5 минут утром и вечером.', 'health', false, 38),
('Увлажняйте воздух', 'Используйте увлажнитель. Это помогает восстановлению слизистых легких.', 'health', true, 39),
('Регулярные анализы', 'Сдайте анализы через месяц после отказа. Видимые улучшения мотивируют продолжать.', 'health', true, 40),

-- Мотивация (motivation)
('Посчитайте экономию', 'Рассчитайте, сколько сэкономите за год. На эти деньги можно купить путевку!', 'motivation', false, 41),
('Фото "до и после"', 'Сфотографируйтесь сегодня. Повторите через месяц. Увидите разницу в цвете лица.', 'motivation', false, 42),
('Список целей', 'Напишите 10 целей, которых достигнете благодаря отказу от курения.', 'motivation', false, 43),
('История успеха', 'Найдите человека, который бросил курить, и попросите поделиться опытом.', 'motivation', true, 44),
('Визуальный календарь', 'Отмечайте каждый день без сигарет стикером. Цепочка мотивирует не прерываться.', 'motivation', false, 45),
('Договор с собой', 'Напишите контракт на бумаге: "Я обязуюсь не курить 30 дней". Подпишите.', 'motivation', false, 46),
('Группа поддержки', 'Присоединитесь к сообществу людей, бросающих курить. Вместе легче.', 'motivation', true, 47),
('Почитайте истории', 'Читайте отзывы людей, которые бросили. Их опыт вдохновляет.', 'motivation', false, 48),
('Спорт-цель', 'Поставьте спортивную цель: пробежать 5 км, подтянуться 10 раз. Здоровые легкие помогут.', 'motivation', true, 49),
('Напомните "зачем"', 'Каждое утро перечитывайте список причин, почему вы бросаете курить.', 'motivation', false, 50);
