INSERT INTO lifehacks (title, description, category, is_premium, likes) VALUES
('Техника 4-7-8', 'При желании закурить: вдох через нос 4 секунды, задержка дыхания 7 секунд, выдох через рот 8 секунд. Повторите 3-4 раза.', 'breathing', false, 0),
('Правило 5 минут', 'Когда хочется курить, скажите себе: "Подожду 5 минут". Обычно желание проходит. Если нет — ещё 5 минут.', 'trigger', false, 0),
('Список причин', 'Напишите 10 причин, почему вы бросаете курить. Перечитывайте список каждый раз, когда хочется сорваться.', 'stress', false, 0),
('Метод резинки', 'Наденьте резинку на запястье. При желании закурить — щёлкните себя резинкой. Мозг переключается на другое ощущение.', 'habit', false, 0),
('Холодная вода', 'Выпейте стакан холодной воды медленными глотками. Это снижает тягу и помогает вывести токсины.', 'trigger', false, 0),
('Визуализация лёгких', 'Закройте глаза и представьте свои лёгкие очищающимися от смол. Дышите глубоко и медленно.', 'breathing', false, 0),
('Копилка для сигарет', 'Каждый раз, когда хотите купить сигареты, кладите эти деньги в копилку. Через месяц увидите результат.', 'money', false, 0),
('Прогулка вместо перекура', 'Замените перекур на 10-минутную прогулку. Свежий воздух и движение снимают стресс лучше сигарет.', 'activity', false, 0),
('Метод таймера', 'Поставьте таймер на 1 час. Обещайте себе не курить до звонка. Постепенно увеличивайте интервалы.', 'habit', false, 0),
('Телефонный друг', 'Найдите друга, который тоже бросает курить. Звоните ему, когда очень хочется закурить. Поддержка критически важна.', 'stress', false, 0);